    computed: {
        filteredLanguages() {
            if (this.activeCategory === 'Tous') {
                return this.languages
            }
            return this.languages.filter(lang => lang.category === this.activeCategory)
        },
        totalLanguages() {
            return this.languages.length
        },
        averageLevel() {
            const sum = this.languages.reduce((acc, lang) => acc + lang.level, 0)
            return Math.round(sum / this.languages.length)
        },
        totalExperience() {
            return this.languages.reduce((acc, lang) => acc + lang.experience, 0)
        }
